import React from 'react';
import './MultySelect.css';

export default class MultySelect extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            suggestions: [],
            text: '',
            elements: [],
            items: this.props.items,
        };
    }

    onTextChanged = (e) => {
        const {items} = this.props;
        const value = e.target.value;
        const arr = this.state.elements;
        let suggestions = [];
        suggestions = items;

        if (value.length > 0) {
            const regex = new RegExp(`^${value}`, 'i');
            let i = 0;
            do {
                suggestions = suggestions.sort().filter(v => regex.test(v) && v !== arr[i]);
                i++;
            }
            while (i<arr.length)
        }
        else {
            suggestions = [];
        }
        this.setState(() => ({suggestions, text: value}));
    };

    handleKeyDown = (e) => { // 1 пункт доп. задания
        const arr = this.state.elements;
        const text = this.state.text;
        const suggs = this.state.suggestions;
        if (suggs.length === 0 && text.length !== 0 && e.key === 'Enter' && !arr.includes(text)) {
            arr.push(text);
            this.setState({
                elements: arr
            });
        }

        if (text.length === 0 && e.key === 'Backspace') {  // 3 пункт доп. задания
            arr.splice(arr.length-1, 1);
            this.setState({
                elements: arr,
            });
        }
    };

    suggestionSelected = (text, index) => {
        const arr = this.state.elements;
        arr.push(text);
        this.setState({
            elements: arr,
            suggestions: this.state.suggestions.filter((item, i) => i !== index),
        });
    };

    eachElement = (item, i) => {
        return (
            <Elements item={item} deleteElement={() => this.deleteElement(i)}/>
        )
    };

    deleteElement = (i) => {
        let suggestions = this.state.suggestions;
        const arr = this.state.elements;
        const arrOfItems = this.state.items;

        if (arrOfItems.includes(arr[i]) === false) {        // 2 пункт доп. задания
            arrOfItems.push(arr[i]);
            alert(arrOfItems.length);
        }

        suggestions.push(arr[i]);
        arr.splice(i, 1);

        const value = document.getElementById("input").value;

        if (value.length > 0) {
            const regex = new RegExp(`^${value}`, 'i');
            suggestions = suggestions.sort().filter(v => regex.test(v));
        }
        else {
            suggestions = [];
        }
        this.setState({
            elements: arr,
            suggestions: suggestions,
            items: arrOfItems,
        });
    };

    renderSuggestions = () => {
        const {suggestions} = this.state;
        if (suggestions.length === 0) {
            return null;
        }

        if (this.state.suggestions) {
            return (
                this.state.suggestions.map((item, i) => (
                    <ListItem
                        items={this.state.items}
                        elements={this.state.elements}
                        key={i}
                        text={item}
                        suggestionSelected={() => this.suggestionSelected(item, i)}
                    />
                )));
        }
    };

    render() {
        const {text} = this.state;
        return (
            <div className="MultySelect">
                <div className="buttons">
                    {this.state.elements.map(this.eachElement)}
                </div>
                <input id="input" value={text} onChange={this.onTextChanged} onKeyDown={this.handleKeyDown} type="text"/>
                {this.renderSuggestions()}
            </div>
        )
    }
}

class ListItem extends React.Component {
    render() {
        return (
            <ul>
                <li onClick={this.props.suggestionSelected}>{this.props.text}</li>
            </ul>
        )
    }
}

class Elements extends React.Component {
    render() {
        return (
            <button className="elementBtn" onClick={this.props.deleteElement}>
                {this.props.item}
                <span className="cross">&#10008;</span>
            </button>
        )
    }
}
